def f(x):
    return x**2 + 2*x
def gradient(x, h=0.001):
    return (f(x+h) - f(x-h)) / (2*h)
grad = gradient(3)
print(f'Gradient: {round(grad, 1)}')

items = [4, 8, 5, 1, 7, 6, 1, 4, 2, 2]
bin_capacity = 10
bins = []
for item in sorted(items, reverse=True):
    placed = False
    for b in bins:
        if sum(b) + item <= bin_capacity:
            b.append(item)
            placed = True
            break
    if not placed:
        bins.append([item])
print(f'Bins: {len(bins)}')

