def calc_mae(actual, forecast):
    errors = [abs(a - f) for a, f in zip(actual, forecast)]
    return sum(errors) / len(errors)

actuals = [100, 110, 105]
forecasts = [102, 108, 107]
print(calc_mae(actuals, forecasts))

def croston_forecast(demand_data, a=0.1, b=0.1):
    z = demand_data[0] if demand_data[0] > 0 else 0.1
    p = 1.0
    for d in demand_data[1:]:
        if d > 0:
            z = a * d + (1 - a) * z
            p = b * 1 + (1 - b) * p
        else:
            p = b * 0 + (1 - b) * p
    return z / p

data = [0, 5, 0, 0, 8, 0, 4]
print(round(croston_forecast(data), 2))

def forecast_bias(actual, forecast):
    errors = [a - f for a, f in zip(actual, forecast)]
    return sum(errors) / len(errors)

