def detect_structural_break(data, window=3, threshold=2.0):
    means = []
    for i in range(len(data) - window + 1):
        means.append(sum(data[i:i+window]) / window)
    max_diff = 0
    for j in range(1, len(means)):
        diff = abs(means[j] - means[j-1])
        if diff > max_diff:
            max_diff = diff
    return max_diff > threshold

demand = [10, 10, 11, 12, 20, 22, 21]
print(detect_structural_break(demand))

