import math

def calc_metric(sample_mean, pop_mean, std_dev, n):
    # Formula: (x-bar - mu) / (s / sqrt(n))
    return (sample_mean - pop_mean) / (std_dev / math.sqrt(n))

def get_value(sample_std_dev, n):
    # Calculation for the denominator of the t-statistic
    return sample_std_dev / (n ** 0.5)

def yates_grundy_variance(sample, y_values, pi_values, pi_ij_values):
    variance = 0
    n = len(sample)
    for i in range(n):
        for j in range(i+1, n):
            term = (pi_values[i] * pi_values[j] - pi_ij_values[i,j]) * ((y_values[i]/pi_values[i] - y_values[j]/pi_values[j]) ** 2)
            variance += term
    return variance

def successive_differences_variance(systematic_sample):
    n = len(systematic_sample)
    if n < 2:
        return 0
    sum_squared_diffs = 0
    for i in range(1, n):
        diff = systematic_sample[i] - systematic_sample[i-1]
        sum_squared_diffs += diff ** 2
    variance = (1 - 1/n) * sum_squared_diffs / (2 * (n - 1))
    return variance

def chapman_estimator(first_capture, second_capture, marked_in_second):
    # Chapman estimator for closed populations
    if marked_in_second == second_capture:
        return float('inf')
    N = ((first_capture + 1) * (second_capture + 1)) / (marked_in_second + 1) - 1
    return N

def sen_yates_grundy_systematic(sample_values, sampling_interval):
    n = len(sample_values)
    if n < 2:
        return 0
    sum_var = 0
    for i in range(n-1):
        for j in range(i+1, n):
            pair_var = (sample_values[i] - sample_values[j])**2
            sum_var += pair_var
    variance = (1 - 1/n) * sum_var / (n * (n - 1))
    return variance

def multiplicity_weight(degree, network_size):
    # Calculate multiplicity weight for network sampling
    if degree == 0:
        return 0
    weight = network_size / degree
    return weight

def leslie_davis_estimator(first_capture, second_capture, third_capture, m12, m13, m23, m123):
    # Leslie-Davis estimator for three samples
    if m123 == 0:
        return float('inf')
    N = (first_capture * second_capture * third_capture) / (m12 * m13 * m23 / m123)
    return N

def jolly_seber_estimate(captures, recaptures, new_marks, total_caught):
    # Simplified Jolly-Seber for open populations
    if recaptures == 0:
        return float('inf')
    survival_rate = recaptures / captures
    new_animals = new_marks / (1 - survival_rate)
    population_estimate = total_caught + new_animals
    return population_estimate

def multiplicity_estimate(reports, network_sizes, degrees):
    # Calculate population size using multiplicity method
    estimates = []
    for i in range(len(reports)):
        if degrees[i] > 0:
            estimate = reports[i] * network_sizes[i] / degrees[i]
            estimates.append(estimate)
    population_estimate = sum(estimates) / len(estimates)
    return population_estimate

def finite_population_correction(population_size, sample_size):
    # Calculate finite population correction factor
    if population_size <= sample_size:
        return 0
    fpc = ((population_size - sample_size) / (population_size - 1)) ** 0.5
    return fpc
