def calculate_stat(outcomes, probabilities):
    # outcomes: list of values (x)
    # probabilities: list of probs (p)
    return sum(x * p for x, p in zip(outcomes, probabilities))

def calc_property(values, probs):
    mean_val = sum(v * p for v, p in zip(values, probs))
    # Sum of p(x) * (x - mean)^2
    return sum(p * ((v - mean_val) ** 2) for v, p in zip(values, probs))
